const Pool = require("generic-pool/lib/Pool");
const Deque = require("generic-pool/lib/Deque");
const PriorityQueue = require("generic-pool/lib/PriorityQueue");
const DefaultEvictor = require("generic-pool/lib/DefaultEvictor");
module.exports = {
  Pool: Pool,
  Deque: Deque,
  PriorityQueue: PriorityQueue,
  DefaultEvictor: DefaultEvictor,
  createPool: function(factory, config) {
    return new Pool(DefaultEvictor, Deque, PriorityQueue, factory, config);
  }
};
